mapper.insert:
insert into t_user (
	user_name, password, sex,real_name,age,create_time,update_time
)
values (
	#{userName}, #{password},#{sex},#{realName},#{age},#{createTime},#{updateTime}
)

mapper.select:
select 

from t_user 


			and uid = #{uid} 


			and	user_name = #{userName}

select 
	count(1)
from t_user 


			and uid = #{uid}


			and user_name = #{userName}


			and age=#{age}


			and sex=#{sex}

select 

from t_user 


			and uid = #{uid}


			and user_name like  like CONCAT(CONCAT('%',#{userName}),'%')


			and real_name like  like CONCAT(CONCAT('%',#{realName}),'%')


			and age=#{age}


			and sex=#{sex}


	order by create_time desc
	limit #{start},#{pageSize}
